package Java_Beginners;

public class Substraction_GivenInput {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int nu1=8;
int nu2=6;
		sub(nu1,nu2);
	}

	private static void sub(int nu1, int nu2) {
		// TODO Auto-generated method stub
		int s=nu1-nu2;
		System.out.println("Sub of"+nu1+"-"+nu2+"="+s);
	}

}
